para.example:
   - Tamsayılar üzerinde bitsel işlemler


<?php
/*
 * Üstteki bölümü yoksay,
 * çıktıyı iyileştirmek için biçemlemekten başka birşey yapmıyor.
 */

$format = '(%1$2d = %1$04b) = (%2$2d = %2$04b)'
        . ' %3$s (%4$2d = %4$04b)' . "\n";

echo <<<EOH
 ---------     ---------  -- ---------
   sonuç         değer    işl   dnm
 ---------     ---------  -- ---------
EOH;


/*
 * Örnekler burada.
 */

$değerler = array(0, 1, 2, 4, 8);
$dnm = 1 + 4;

echo "\n Bitsel VE \n";
foreach ($değerler as $değer) {
    $sonuç = $değer & $dnm;
    printf($format, $sonuç, $değer, '&', $dnm);
}

echo "\n Bitsel Ayrıcalıklı VEYA \n";
foreach ($değerler as $değer) {
    $sonuç = $değer | $dnm;
    printf($format, $sonuç, $değer, '|', $dnm);
}

echo "\n Bitsel Ayrıcalıklı VEYA (XOR) \n";
foreach ($değerler as $değer) {
    $sonuç = $değer ^ $dnm;
    printf($format, $sonuç, $değer, '^', $dnm);
}
?>


   &example.outputs;


 ---------     ---------  -- ---------
   sonuç         değer    işl   dnm
 ---------     ---------  -- ---------
 Bitsel VE
( 0 = 0000) = ( 0 = 0000) & ( 5 = 0101)
( 1 = 0001) = ( 1 = 0001) & ( 5 = 0101)
( 0 = 0000) = ( 2 = 0010) & ( 5 = 0101)
( 4 = 0100) = ( 4 = 0100) & ( 5 = 0101)
( 0 = 0000) = ( 8 = 1000) & ( 5 = 0101)

 Bitsel Ayrıcalıklı VEYA
( 5 = 0101) = ( 0 = 0000) | ( 5 = 0101)
( 5 = 0101) = ( 1 = 0001) | ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) | ( 5 = 0101)
( 5 = 0101) = ( 4 = 0100) | ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) | ( 5 = 0101)

 Bitsel Ayrıcalıklı VEYA (XOR)
( 5 = 0101) = ( 0 = 0000) ^ ( 5 = 0101)
( 4 = 0100) = ( 1 = 0001) ^ ( 5 = 0101)
( 7 = 0111) = ( 2 = 0010) ^ ( 5 = 0101)
( 1 = 0001) = ( 4 = 0100) ^ ( 5 = 0101)
(13 = 1101) = ( 8 = 1000) ^ ( 5 = 0101)

- Tamsayılarda bit kaydırma
<?php /* * Bunlar örnektir. */
echo "\n--- POZİTİF TAMSAYILARDA SAĞA BİT KAYDIRMA ---\n";
$dgr = 4; $hane = 1; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'işaret bitinin kopyası sola kaydırıldı');
$dgr = 4; $hane = 2; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane);
$dgr = 4; $hane = 3; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'bitler sağa kaydırıldı');
$dgr = 4; $hane = 4; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'yukarıdakiyle aynı; 0\'dan sonrası kaydırılamaz');
echo "\n--- NEGATİF TAMSAYILARDA SAĞA BİT KAYDIRMA ---\n";
$dgr = -4; $hane = 1; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'işaret bitinin kopyası sola kaydırıldı');
$dgr = -4; $hane = 2; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'bitler sağa kaydırıldı');
$dgr = -4; $hane = 3; $snç = $dgr >> $hane; p($snç, $dgr, '>>', $hane, 'yukarıdakiyle aynı; -1\'dan sonrası kaydırılamaz');
echo "\n--- POZİTİF TAMSAYILARDA SOLA BİT KAYDIRMA ---\n";
$dgr = 4; $hane = 1; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane, 'sağ taraf sıfırlarla doldurulur');
$dgr = 4; $hane = (PHP_INT_SIZE * 8) - 4; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane);
$dgr = 4; $hane = (PHP_INT_SIZE * 8) - 3; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane, 'işaret bitleri dışa kaydırılır');
$dgr = 4; $hane = (PHP_INT_SIZE * 8) - 2; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane, 'bitler sola kaydırıldı');
echo "\n--- NEGATİF TAMSAYILARDA SOLA BİT KAYDIRMA ---\n";
$dgr = -4; $hane = 1; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane, 'sağ taraf sıfırlarla doldurulur');
$dgr = -4; $hane = (PHP_INT_SIZE * 8) - 3; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane);
$dgr = -4; $hane = (PHP_INT_SIZE * 8) - 2; $snç = $dgr << $hane; p($snç, $dgr, '<<', $hane, 'işaret biti dahil, bitler soldan dışa kaydırıldı');
/* * Bundan sonrasını yoksayın; çıktıyı biçemlemekten başka bir şey yapmıyor. */
function p($snç, $dgr, $op, $hane, $ilt = '') { $format = '%0' . (PHP_INT_SIZE * 8) . "b\n";
printf("İfade: %d = %d %s %d\n", $snç, $dgr, $op, $hane);
echo " Onluk:\n"; printf(" değer=%d\n", $dgr); printf(" sonuç=%d\n", $snç);
echo " İkilik:\n"; printf(' değer=' . $format, $dgr); printf(' sonuç=' . $format, $snç);
if ($ilt) { echo " DİKKAT: $ilt\n"; }
echo "\n"; } ?>
&example.outputs.32bit;
