refentry.refsect1:
&reftitle.description;

 boolclasskit_method_redefine
 stringsinifadi
 stringyontemadi
 stringdegistirgeler
 stringkod
 intbayraklarCLASSKIT_ACC_PUBLIC

&note.classkit.selfmanipulation;
&warn.experimental.func;

  &reftitle.examples;



    - classkit_method_redefine örneği


<?php
class Example {
    function foo() {
        return "foo!\n";
    }
}

// Example nesnesi oluştur
$e = new Example();

// Example::foo() (yeniden tanımlamadan önce yöntem çıktısı)
echo "Before: " . $e->foo();

// 'foo' yöntemini yeniden tanımla
classkit_method_redefine(
    'Example',
    'foo',
    '',
    'return "bar!\n";',
    CLASSKIT_ACC_PUBLIC
);

// Example::foo() (yeniden tanımlamadan sonra yöntem çıktısı)
echo "After: " . $e->foo();
?>


    &example.outputs;


Before: foo!
After: bar!
