refentry.refsect1:
&reftitle.notes;
&note.uses-autoload;



  property_exists işlevi __get
  kullanarak sihirli olarak ulaşılabilen özellikeri saptayamaz.

  &reftitle.examples;



    - property_exists örneği


<?php

class myClass {
    public $mine;
    private $xpto;
    static protected $test;

    static function test() {
        var_dump(property_exists('myClass', 'xpto')); //true
    }
}

var_dump(property_exists('myClass', 'mine'));   //true
var_dump(property_exists(new myClass, 'mine')); //true
var_dump(property_exists('myClass', 'xpto'));   //true, PHP 5.3.0'dan beri
var_dump(property_exists('myClass', 'bar'));    //false
var_dump(property_exists('myClass', 'test'));   //true, PHP 5.3.0'dan beri
myClass::test();

?>
