refentry.refsect1:
&reftitle.description;
boolftp_rename resourceftp_akımı stringeskisi stringyenisi
ftp_rename işlevi, FTP sunucusundaki belirtilen dosya veya dizinin adını değiştirir.

  &reftitle.examples;



    - ftp_rename örneği


<?php
$eskisi = 'somefile.txt.bak';
$yenisi = 'somefile.txt';

// Temel bağlantıyı kuralım
$conn_id = ftp_connect($ftp_server);

// kullanıcı adı ve parola ile oturum açalım
$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);

// Dosya ismini $yenisi yapalım
if (ftp_rename($conn_id, $eskisi, $yenisi)) {
 echo "$eskisi dosyasının ismi $yenisi olarak değiştirildi\n";
} else {
 echo "$eskisi dosyasını ismi $yenisi ile değiştirilirken bir sorun çıktı\n";
}

// bağlantıyı kapatalım
ftp_close($conn_id);
?>
