refentry.refsect1:
&reftitle.parameters;



  &gd.image.description;

   x



     Pikselin X konumu.




   y



     Pikselin Y konumu.

  &reftitle.examples;



    - Hesapla RGB değeri bileşenlerine ayırmak


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);
$r = ($rgb >> 16) & 0xFF;
$g = ($rgb >> 8) & 0xFF;
$b = $rgb & 0xFF;

var_dump($r, $g, $b);
?>


    &example.outputs.similar;


int(119)
int(123)
int(180)




    - imagecolorsforindex ile RGB değeri
    bileşenlerine ayırmak


<?php
$im = imagecreatefrompng("php.png");
$rgb = imagecolorat($im, 10, 15);

list($r, $g, $b, $alpha) = imagecolorsforindex($rgb);

var_dump($r, $g, $b, $alpha);
?>


    &example.outputs.similar;


int(119)
int(123)
int(180)
int(127)
