refentry.refsect1:
&reftitle.description;
resourceboolmysql_db_query stringveritabanı stringsorgu resourcebağlantı_belirteci&null;
mysql_db_query bir veritabanı seçip üzerinde bir sorgu çalıştırır.

  &reftitle.examples;

   - mysql_db_query yerine kullanılabilecek kod
   örneği


<?php

if (!$link = mysql_connect('mysql_host', 'mysql_user', 'mysql_password')) {
    echo 'mysql\'e bağlanamadı';
    exit;
}

if (!mysql_select_db('mysql_dbname', $link)) {
    echo 'Veritabanını seçemedi';
    exit;
}

$sql    = 'SELECT foo FROM bar WHERE id = 42';
$result = mysql_query($sql, $link);

if (!$result) {
    echo "Veritabanı hatası, veritabanını sorgulayamıyor\n";
    echo 'MySQL Hatası: ' . mysql_error();
    exit;
}

while ($row = mysql_fetch_assoc($result)) {
    echo $row['foo'];
}

mysql_free_result($result);

?>
