  &reftitle.examples;



    - htmlentities örneği


<?php
$str = "<b>Tek</b> bir 'tırnak'";

// Çıktısı: &lt;b&gt;Tek&lt;/b&gt; bir 'tırnak'
echo htmlentities($str);

// Çıktısı: &lt;b&gt;Tek&lt;/b&gt; bir &#039;quote&#039;
echo htmlentities($str, ENT_QUOTES);
?>
