refentry.refsect1:
&reftitle.description;

 booltidyNode::isAsp




 Düğüm bir ASP düğümü mü diye bakar.

  &reftitle.examples;



    - Karışık bir HTML belgeden ASP kodunu çıkarmak


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>Başlık</title>'; ?>
<#
  /* JSTE kodu */
  alert('Merhaba Dünya');
#>
</head>
<body>

<?php
  // PHP code
  echo 'merhaba dünya!';
?>

<%
  /* ASP kodu */
  response.write("Merhaba Dünya!")
%>

<!-- Açıklamalar -->
Merhaba Dünya
</body></html>
HTML dışı
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html(), 'asp');

function get_nodes($node, $node_type) {

    // Düğüm istenen türde mi?
    if($node->{"is$node_type"}()) {
        echo "\n\n# $node_type node #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Düğümün çocukları var mı?
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child, $node_type);
        }
    }
}

?>


    &example.outputs;


# asp node #1
<%
  /* ASP kodu */
  response.write("Merhaba Dünya!")
%>
