refentry.refsect1:
  &reftitle.examples;

   - Bir arşivi açıp içeriğini dökmek


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip');
if ($res === TRUE) {
    echo 'Tamam';
    $zip->extractTo('test');
    $zip->close();
} else {
    echo 'Olmadı, kod:' . $res;
}
?>




   - Bir arşiv oluşturmak


<?php
$zip = new ZipArchive;
$res = $zip->open('test.zip', ZipArchive::CREATE);
if ($res === TRUE) {
    $zip->addFromString('test.txt', 'Bu dizge dosyanın içeriğidir.');
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'Tamam';
} else {
    echo 'Olmadı';
}
?>




   - Geçici bir arşiv oluşturma


<?php
$name = tempnam(sys_get_temp_dir(), "FOO");
$zip = new ZipArchive;
$res = $zip->open($name, ZipArchive::OVERWRITE); // boş dosya budanmaz
if ($res === TRUE) {
    $zip->addFile('data.txt', 'entryname.txt');
    $zip->close();
    echo 'Tamam';
} else {
    echo 'Olmadı';
}
?>
